package com.chainsys.intro;

public class Evennumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
       evennumber();
	}
   public static void evennumber()
   {
	   System.out.println("EVEN NUMBER IS : ");
	for(int i=1;i<=100;i++)
	{
		if(i % 2 ==0)
		{
	  System.out.print(i);
		}
		else
	   {
			System.out.println(" ");
	   }
   }
 }
}   
